mapper.select:
select
spu_attr_id, spu_id, attribute_id, attribute_value
from spu_attribute_value


        and spu_attr_id = #{spuAttrId}


        and spu_id = #{spuId}


        and attribute_id = #{attributeId}


        and attribute_value = #{attributeValue}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from spu_attribute_value


        and spu_attr_id = #{spuAttrId}


        and spu_id = #{spuId}


        and attribute_id = #{attributeId}


        and attribute_value = #{attributeValue}
